package main

import (
	"fmt"
	"log"

	"github.com/code-brew-lab/gonq.git/internal/core/dns"
)

func main() {
	flags := dns.NewFlags(true, true)
	header := dns.NewHeader(flags, 1)

	bytes, err := header.BinaryMarshaler()
	if err != nil {
		log.Fatalln(err)
	}

	for i := 0; i < len(bytes); i++ {
		fmt.Printf("%08b\n", bytes[i])
	}
}

